  time, txSize, size, 
  difficulty, weight, version, 
  nonce, previousblock, nextblock
  )
values (#{bid,jdbcType=INTEGER}, #{blockhash,jdbcType=CHAR}, #{height,jdbcType=INTEGER}, 
  #{time,jdbcType=TIMESTAMP}, #{txsize,jdbcType=SMALLINT}, #{size,jdbcType=INTEGER}, 
  #{difficulty,jdbcType=DOUBLE}, #{weight,jdbcType=REAL}, #{version,jdbcType=VARCHAR}, 
  #{nonce,jdbcType=INTEGER}, #{previousblock,jdbcType=CHAR}, #{nextblock,jdbcType=CHAR}
  )
